CREATE TABLE IF NOT EXISTS product_families (
  id SERIAL PRIMARY KEY,
  name TEXT NOT NULL,
  brand TEXT,
  category TEXT,
  description TEXT,
  "defaultImage" TEXT,
  attributes JSONB,
  created_at TIMESTAMP DEFAULT NOW(),
  updated_at TIMESTAMP DEFAULT NOW()
);

CREATE UNIQUE INDEX IF NOT EXISTS product_families_name_brand_key ON product_families(name, brand);

CREATE TABLE IF NOT EXISTS products (
  id SERIAL PRIMARY KEY,
  sku TEXT UNIQUE NOT NULL,
  name TEXT NOT NULL,
  description TEXT,
  category TEXT,
  family_id INTEGER,
  unit_cost NUMERIC,
  currency TEXT,
  msrp NUMERIC,
  margin NUMERIC,
  base_margin NUMERIC,
  tier_base_discount NUMERIC,
  tier_plus_discount NUMERIC,
  volume_breaks JSONB,
  lead_time_days INTEGER,
  stock_band TEXT,
  facets JSONB,
  variant_facets JSONB,
  compat_requires JSONB,
  compat_blocks JSONB,
  bundle_components TEXT,
  supplier JSONB,
  pricing JSONB,
  created_at TIMESTAMP DEFAULT NOW(),
  updated_at TIMESTAMP DEFAULT NOW()
);

ALTER TABLE products
  ADD COLUMN IF NOT EXISTS family_id INTEGER,
  ADD CONSTRAINT products_family_id_fkey FOREIGN KEY (family_id) REFERENCES product_families(id) ON DELETE SET NULL ON UPDATE CASCADE;

